onMouseMove(e) {
    if(!this.dont_mouse) return
    let x = e.clientX
    let y = e.clientY

    gsap.to(this.models[this.current].rotation, {
        duration: 3,
        ease: 'expo.out',
        y: gsap.utils.mapRange(0, window.innerWidth, -0.5, 0.5, x),
        x: gsap.utils.mapRange(0, window.innerHeight, -0.5, 0.5, y),
    })
}

