import { gsap } from 'gsap'

export default class Ambient {
    constructor(gl_app, models) {
        this.app = gl_app
        this.models = models
    }
}

constructor(gl_app, models) {
    ...
    //utilities
    this.walk_distance = 3.5
    this.duration = 2.2
    this.dont_mouse = true 
    this.animating = false

    //manage models
    this.current = 0
    this.next = this.current + 1
    this.previous =  this.models.length
}

constructor(gl_app, models) {
    ...
    this.DOM = {
        $body: document.querySelector('body'),
        $btn_prev: document.querySelector('.prev'),
        $btn_next: document.querySelector('.next'),
    }
    ...
}

bindEvents() {
    // previous button
    this.DOM.$btn_prev.addEventListener('click', ()=> {
        if(this.animating) return
        if(this.current == 0 ) { this.DOM.$body.classList.add('is-first'); return }
        this.DOM.$body.classList.remove('is-first','is-last')
        this.previous = this.current
        this.current--
    })

    // next button
    this.DOM.$btn_next.addEventListener('click', ()=> {
        if(this.animating) return
        if(this.current == this.models.length - 1 ) { this.DOM.$body.classList.add('is-last'); return }
        this.DOM.$body.classList.remove('is-first','is-last')
        this.previous = this.current
        this.current++
    })
}

travelScene(distance) {
    this.animating = true
    gsap.timeline({ defaults: { duration: this.duration, ease: 'power4.inOut' }, onComplete: () => {
        this.animating = false
    } })

    //entering
    .to(this.models[this.current].position, { y: 0 })
    .fromTo(this.models[this.current].rotation, { y: Math.PI , x: Math.PI * 0.5 }, { y: 0, x: 0 }, '<')

    //leaving
    .to(this.models[this.previous].position, { y: distance }, 0)
    .fromTo(this.models[this.previous].rotation, { x: 0, y: 0 }, { y: - Math.PI , x: - Math.PI * 0.5 }, '<')
}

bindEvents() {
    // previous button
    this.DOM.$btn_prev.addEventListener('click', ()=> {
        ...
        this.travelScene(- this.walk_distance)
    })

    // next button
    this.DOM.$btn_next.addEventListener('click', ()=> {
        ...
        this.travelScene(this.walk_distance)
    })
}

import * as THREE from 'three'

export default class Ambient {
    constructor(gl_app, envMapTexture) {
        this.app = gl_app
        this.envMapTexture = envMapTexture
    }
}

constructor(gl_app, envMapTexture) {
    ...
    this.setLight()
}

setLight() {
    this.ambientLight = new THREE.AmbientLight('#ffffff', 80)
    this.app.scene.add(this.ambientLight)
}

constructor(gl_app, envMapTexture) {
    ...
    this.setEnvMap()
}

setEnvMap() {
    this.environmentMap = {}
    this.environmentMap.intensity = 7
    this.environmentMap.texture =  this.envMapTexture
    this.app.scene.environment = this.environmentMap.texture

    this.environmentMap.updateMaterials = () => {
        this.app.scene.traverse((child) => {
            if(child instanceof THREE.Mesh && child.material instanceof THREE.MeshStandardMaterial) {
                child.material.envMap =  this.environmentMap.texture
                child.material.envMapIntensity = this.environmentMap.intensity
                child.material.needsUpdate = true
            }
        })
    }

    this.environmentMap.updateMaterials()
}

this.sources = [
    {
        name: 'shape1',
        url: Shape1,
        type: 'gltf'
    },
    {
        name: 'shape2',
        url: Shape2,
        type: 'gltf'
    },
    {
        name: 'shape3',
        url: Shape3,
        type: 'gltf'
    },
    {
        name: 'shape4',
        url: Shape4,
        type: 'gltf'
    },
    {
        name: 'environmentMapTexture',
        type: 'cubeTexture',
        path: [px, nx, py, ny, pz, nz]
    }
]

instanceLoaders() {
    this.loaders = {}

    this.loaders.dracoLoader = new DRACOLoader()
    this.loaders.dracoLoader.setDecoderPath('https://www.gstatic.com/draco/versioned/decoders/1.5.5/')

    this.loaders.gltfLoader = new GLTFLoader()
    this.loaders.gltfLoader.setDRACOLoader(this.loaders.dracoLoader)


    this.loaders.cubeTextureLoader = new THREE.CubeTextureLoader()
}

loadAssets() {
    this.objs = []
    this.items = []

    for (const source of this.sources) {
        if(source.type === 'gltf') {
            this.loaders.gltfLoader.load(source.url, (file) => {
                this.objs.push({ name: source.name, model: file })
                this.sourceLoaded(source,file)
            })
        } else if (source.type === 'cubeTexture') {
            this.loaders.cubeTextureLoader.load(source.path, (file) => {
                this.sourceLoaded(source, file)
            })
        }
    }
}

sourceLoaded(source, file) {
    this.items[source.name] = file
    this.loaded++
    if(this.loaded === this.to_load) {
        this.isReady();
    }
}

