* {
	box-sizing: border-box;
	font-family: 'Archivo', sans-serif;
	margin: 0;
	padding: 0;
	color: var(--white);
}
ul {
	list-style: none;
}

button {
	background: transparent;
	border: none;
	cursor: pointer;
	height: max-content;
	width: max-content;
}

canvas#sketch {
	position: fixed;
	left: 0;
	top: 0;
	width: 100vw;
	height: 100vh;
	z-index: 2;
	pointer-events: none;
	clip-path: inset(2rem round 2rem);
}

.logo {
	position: fixed;
	top: var(--gap-y);
	left: var(--gap-x);
	z-index: 3;
	width: 6.5rem;
}

.cta {
	position: absolute;
	top: var(--gap-y);
	right: var(--gap-x);
	display: flex;
	align-items: center;
	justify-content: center;
	background: var(--white);
