}

.content {
	position: fixed;
	left: 0;
	top: 0;
	width: 100vw;
	height: 100vh;
}

.holder {
	position: relative;
}


.content__holder {
	align-items: center;
	display: flex;
	height: 100%;
	width: 100%;
