}

.bg {
	width: 100vw;
	height: 100vh;
	position: absolute;
	left: 0;
	top: 0;
	background: #050F7C;
	clip-path: inset(2rem round 2rem);
	display: flex;
	align-items: flex-end;
	transition: background 2s ease-in-out;
}

.bg__grad {
	background: linear-gradient(0deg, #161616b0 18.23%, #ffffff00 100%);
	height: 66vh;
	width: 100%;
}

.marquees {
	left: 0;
	overflow: hidden;
	position: absolute;
	width: 100vw;
}

ul.marquees__holder {
	display: grid;
	grid-template-columns: 1fr;
}

.marquees__travel {
	display: inline-flex;
	grid-area: 1 / 1 / 2 / 2;
	white-space: nowrap;
	will-change: transform;
	opacity: 0;
	transition: opacity 1s ease-in-out 0s;
}

.mq {
	--time: 16s;
	animation: marquee var(--time) infinite linear;
}

.texts {
	position: absolute;
	z-index: 1;
