}

.trgs__trg svg {
	overflow: visible;
	transform-origin: 50% 50%;
	transition:  scale .225s ease-in-out;
}

.trgs__trg svg rect {
	fill: rgba(0, 0, 0, .3);
	transition:  fill .225s ease-in-out, scale .225s ease-in-out;
